INSERT INTO Tomes(code,name,date_VF,date_VO,volume,pages,resumer,cover,EditeurId,SerieId) VALUES (9782871294146,'[Naruto Uzumaki]','2002-03-07','2002-03-07',187,0,'Naruto est un garçon un peu spécial. Il est toujours seul et son caractère fougueux ne l''aide pas à se faire apprécier de son village. Malgré cela, il garde l''ambition de devenir un maître Hokage, la plus haute distinction dans l''ordre des ninjas, et ainsi obtenir la reconnaissance de ses pairs.','default.png',7,116);
INSERT INTO Tomes(code,name,date_VF,date_VO,volume,pages,resumer,cover,EditeurId,SerieId) VALUES (9782871294177,'[Un client embarrassant]','2002-04-11','2002-04-11',205,0,'Sasuke, Sakura et Naruto passent un test dont le but est de s''emparer des clochettes que détient le professeur kakashi. Il leur fait bien réaliser leur inferiorité et leur manque d''expérience et finit par leur annoncer qu''ils n''ont aucune chance de devenir Ninjas...','default.png',7,116);
INSERT INTO Tomes(code,name,date_VF,date_VO,volume,pages,resumer,cover,EditeurId,SerieId) VALUES (9782871294276,'[Se battre pour ses rêves!!]','2002-07-04','2002-07-04',204,0,'Avec Sasuke et Sakura, Naruto, le pire garnement de l''école de ninjas de Konoha, un village caché, réussit avec brio le test de survie imposé par maître Kakashi. Les trois jeunes gens forment une vraie équipe, mais ils ne sont encore que des ninjas de rang inférieur.','default.png',7,116);
